sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({
    api,
    event,
    helpers,
    imports
}) {

    var href = (event.payload.action.href).split('/');
    if (href[0] == 'updatereport') {
        helpers.navigate.to(href[0], {
            'table': href[1],
            'sysId': href[2]
        });
    } else {
        helpers.navigate.to(href[0], {
            'table': href[1],
            'sysId': href[2],
            'kbSysId': href[3]
        });

    }

}
